import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setSearchTerm } from '../stores/SearchTerm';

const SearchBar = () => {
    const searchTerm = useSelector(state => state.searchTerm.term);
    const dispatch = useDispatch();
    useEffect(() => {
        console.log(searchTerm)
    })
  return (
    <div className='search-bar-section'>
        <input type="text" placeholder='Enter City ... ' value={searchTerm} onChange={(e) => dispatch(setSearchTerm(e.target.value))} />
    </div>
  );
};

export default SearchBar;
